import styled from "styled-components";

export const Card = styled.div`
  max-height: 100%;
  max-width: 256px;
  display: inline-flex;
  padding: 24px;
  flex-direction: column;
  align-items: flex-start;
  border-radius: 12px;
  background-color: #e5ccff;
  .details {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
  }
  h1 {
    margin: 0;
    font-family: Source Code Pro;
    font-size: 18px;
    font-style: normal;
    font-weight: 500;
    line-height: normal;
    text-align: left;
    margin-bottom: 8px;
  }
  h2 {
    font-feature-settings: "clig" off, "liga" off;
    font-family: Source Code Pro;
    font-size: 14px;
    font-style: normal;
    font-weight: 400;
    line-height: normal;
    margin: 0;
    text-align: left;
    margin-bottom: 16px;
  }
  p {
    font-family: Source Code Pro;
    font-size: 14px;
    font-style: normal;
    font-weight: 400;
    line-height: normal;
    margin: 0;
    line-height: 17.6px;
    text-align: left;
    margin-bottom: 20px;
  }
  div {
    display: flex;
    gap: 21px;
    align-items: center;
    justify-content: flex-end;
    margin-left: auto;
  }
  a {
    text-decoration: none;
  }
`;
